<?phpswitch($_REQUEST['cb_Suche']){	case 'Endkapital':		$Suchwert = $_REQUEST['cb_Suche'];		$Anlagekapital = $_REQUEST['tb_Anlagekapital'];		$Zinssatz = $_REQUEST['tb_Zinssatz'];		$Laufzeit = $_REQUEST['tb_Laufzeit'];				$Endkapital = $Anlagekapital * pow(($Zinssatz / 100) + 1, $Laufzeit);		break;	case 'Anlagekapital':		$Suchwert = $_REQUEST['cb_Suche'];		$Zinssatz = $_REQUEST['tb_Zinssatz'];		$Laufzeit = $_REQUEST['tb_Laufzeit'];				$Endkapital = $_REQUEST['tb_Endkapital'];		$Anlagekapital = $Endkapital / pow(($Zinssatz / 100) + 1, $Laufzeit);	break;	case 'Laufzeit':		$Suchwert = $_REQUEST['cb_Suche'];		$Zinssatz = $_REQUEST['tb_Zinssatz'];		$Endkapital = $_REQUEST['tb_Endkapital'];		$Anlagekapital = $_REQUEST['tb_Anlagekapital'];				$Laufzeit = round(log($Endkapital / $Anlagekapital) / log(($Zinssatz / 100) + 1));			break;	case 'Zinssatz':		$Suchwert = $_REQUEST['cb_Suche'];		$Endkapital = $_REQUEST['tb_Endkapital'];		$Anlagekapital = $_REQUEST['tb_Anlagekapital'];				$Laufzeit = $_REQUEST['tb_Laufzeit'];		$Zinssatz = round(100 * (pow($Endkapital/$Anlagekapital,1/$Laufzeit) - 1));			break;}$response = array(	'Suchwert' => $Suchwert,	'Anlagekapital' => $Anlagekapital,	'Zinssatz' => $Zinssatz,	'Laufzeit' => $Laufzeit,	'Endkapital' => $Endkapital);$Jahr = 1;$Ergebnisse = array();$StartkapitalJahr = $Anlagekapital;$ZinsertragJahrKumuliert = 0;  for($Jahr=1;$Jahr<=$Laufzeit;$Jahr++){	$EndkapitalJahr = $Anlagekapital * pow(($Zinssatz / 100) + 1, $Jahr);	$ZinsertragJahr = $EndkapitalJahr - $StartkapitalJahr;	$ZinsertragJahrKumuliert += $ZinsertragJahr;	$ErgebnisJahr = array(		'Jahr' => $Jahr,		'StartkapitalJahr' => $StartkapitalJahr,		'ZinsertragJahr' => $ZinsertragJahr,		'ZinsertragJahrKumuliert' => $ZinsertragJahrKumuliert,		'EndkapitalJahr' => $EndkapitalJahr	);	$StartkapitalJahr = $EndkapitalJahr;	$Ergebnisse[]=$ErgebnisJahr;}session_start();$response['Session'] = session_id();$_SESSION['Rechner'] = $response;$_SESSION['Ergebnisse'] = $Ergebnisse;echo json_encode($response);?>