<?phpdeclare(strict_types=1);error_reporting(E_ALL ^ E_DEPRECATED);require_once './classZinsrechner.php';// Beispiel: Formulardaten validieren und verarbeiten$response = array();$Ergebnisse = array();if ($_SERVER['REQUEST_METHOD'] === 'POST') {    $Suchwert = filter_input(INPUT_POST, 'cb_Suche', FILTER_SANITIZE_STRING);    $Anlagekapital = filter_input(INPUT_POST, 'tb_Anlagekapital', FILTER_VALIDATE_FLOAT);    $Zinssatz = filter_input(INPUT_POST, 'tb_Zinssatz', FILTER_VALIDATE_FLOAT);    $Laufzeit = filter_input(INPUT_POST, 'tb_Laufzeit', FILTER_VALIDATE_FLOAT);    $Endkapital = filter_input(INPUT_POST, 'tb_Endkapital', FILTER_VALIDATE_FLOAT);    if ($Suchwert !== false && $Anlagekapital !== false && $Zinssatz !== false && $Laufzeit !== false && $Endkapital !== false) {        $rechner = new Zinsrechner($Suchwert, $Anlagekapital, $Zinssatz, $Laufzeit, $Endkapital);        $response = $rechner->getResponseSingle();        $Ergebnisse = $rechner->getResponseTable();    }}session_start();$response['Session'] = session_id();$_SESSION['Rechner'] = $response;$_SESSION['Ergebnisse'] = $Ergebnisse;echo json_encode($response);?>